mapper.update:
UPDATE article
category_id = #{categoryId}, title = #{title}, summary = #{summary}, content = #{content}, cover_image = #{coverImage}, is_top = #{isTop}, is_recommend = #{isRecommend}, status = #{status}, publish_time = #{publishTime},
WHERE id = #{id}

mapper.select:
SELECT a.*, c.name as category_name, u.nickname as author_name, u.avatar as author_avatar
FROM article a
LEFT JOIN category c ON a.category_id = c.id
LEFT JOIN user u ON a.user_id = u.id

    AND a.user_id = #{userId}
    AND a.category_id = #{categoryId}
    AND a.status = #{status}

        AND (a.title LIKE CONCAT('%', #{keyword}, '%') 
        OR a.summary LIKE CONCAT('%', #{keyword}, '%'))


ORDER BY a.is_top DESC, a.publish_time DESC
LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM article a

    AND a.user_id = #{userId}
    AND a.category_id = #{categoryId}
    AND a.status = #{status}

        AND (a.title LIKE CONCAT('%', #{keyword}, '%') 
        OR a.summary LIKE CONCAT('%', #{keyword}, '%'))
