mapper.update:
UPDATE note
content = #{content}, images = #{images}, status = #{status},
WHERE id = #{id}

mapper.select:
SELECT n.*, u.nickname as author_name, u.avatar as author_avatar FROM note n LEFT JOIN user u ON n.user_id = u.id
n.status = 1 AND n.user_id = #{userId}
ORDER BY n.create_time DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM note

    status = 1
    AND user_id = #{userId}
